
!function(l){
	"use strict";
	l("#sidebarToggle").on("click",function(o){
		o.preventDefault(),
		l("body").toggleClass("sidebar-toggled"),
		l(".sidebar").toggleClass("toggled")
	}),
	l("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel",function(o){
		if(768<l(window).width()){
			var e=o.originalEvent,
			t=e.wheelDelta||-e.detail;
			this.scrollTop+=30*(t<0?1:-1),
			o.preventDefault()
		}
	}),
	l(document).on("scroll",function(){
		100<l(this).scrollTop()?l(".scroll-to-top").fadeIn():l(".scroll-to-top").fadeOut()
	}),
	l(document).on("click","a.scroll-to-top",function(o){
		var e=l(this);
		l("html, body").stop().animate({
			scrollTop:l(e.attr("href")).offset().top
		},1e3,"easeInOutExpo"),
		o.preventDefault()
	})
}(jQuery);


// my code
function delSRD(el){ // delete srd at add page
	el.parentNode.remove();
}
function delSRDAjax(el, id, url, csrf){ // delete srd at edit page with ajax
	if($('.ser-req-docs div').length > 1){
		$('#loading').show(); $('body').addClass("blur-body"); 
	    $.ajax({
	    	data:{
	    		_token: csrf,
	    		id:id
	    	},
	    	type:'POST',
	    	url:url,
	    	dataType: 'html',
	    	success: function (data) {
	    		$('#loading').hide(); $('body').removeClass("blur-body");
	    		if(data == true) {
	    			el.parentNode.remove();
	    		}
	        },
	        error: function (data, textStatus, errorThrown) {
		        console.log(data);

		    },
	    });
	}
}

